import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

const cardColors = ['#FF5733', '#33FF57', '#3357FF', '#F39C12', '#8E44AD', '#1ABC9C', '#E74C3C', '#2ECC71'];

const duplicateAndShuffle = () => {
  const colors = [...cardColors, ...cardColors];
  for (let i = colors.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [colors[i], colors[j]] = [colors[j], colors[i]];
  }
  return colors.map((color, index) => ({ id: index, color, flipped: false, matched: false }));
};

function App() {
  const [cards, setCards] = useState(duplicateAndShuffle());
  const [flipped, setFlipped] = useState([]);
  const [scores, setScores] = useState({ player1: 0, player2: 0 });
  const [currentPlayer, setCurrentPlayer] = useState(1);
  const [gameOver, setGameOver] = useState(false);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/games');
      setHistory(res.data);
    } catch (error) {
      console.error('Error fetching history:', error);
    }
  };

  const handleCardClick = (index) => {
    if (flipped.length === 2 || cards[index].flipped || cards[index].matched) return;

    const newCards = [...cards];
    newCards[index].flipped = true;
    const newFlipped = [...flipped, index];
    setCards(newCards);
    setFlipped(newFlipped);

    if (newFlipped.length === 2) {
      const [first, second] = newFlipped;
      if (newCards[first].color === newCards[second].color) {
        newCards[first].matched = true;
        newCards[second].matched = true;
        const updatedScores = { ...scores };
        updatedScores[`player${currentPlayer}`] += 1;
        setTimeout(() => {
          setScores(updatedScores);
          setCards(newCards);
          setFlipped([]);
          checkGameOver(newCards, updatedScores);
        }, 700);
      } else {
        setTimeout(() => {
          newCards[first].flipped = false;
          newCards[second].flipped = false;
          setCards(newCards);
          setFlipped([]);
          setCurrentPlayer(currentPlayer === 1 ? 2 : 1);
        }, 1000);
      }
    }
  };

  const checkGameOver = async (cards, scores) => {
    if (cards.every(card => card.matched)) {
      setGameOver(true);
      const result = {
        player1: "Player 1",
        player2: "Player 2",
        score1: scores.player1,
        score2: scores.player2,
        winner:
          scores.player1 > scores.player2
            ? 'Player 1'
            : scores.player2 > scores.player1
            ? 'Player 2'
            : 'Draw',
      };
      try {
        await axios.post('http://localhost:5000/api/games', result);
        fetchHistory();
      } catch (err) {
        console.error('Error saving result:', err);
      }
    }
  };

  const resetGame = () => {
    setCards(duplicateAndShuffle());
    setFlipped([]);
    setScores({ player1: 0, player2: 0 });
    setCurrentPlayer(1);
    setGameOver(false);
  };

  return (
    <div className="container">
      <div className="logo"><i className="fas fa-palette"></i> <span>Color Memory</span></div>
      <h1>🎯 Color Memory Game</h1>

      <div className="turn-indicator">
        <div className={`player ${currentPlayer === 1 ? 'active' : ''}`}>🎈 Player 1</div>
        <div className={`player ${currentPlayer === 2 ? 'active' : ''}`}>🎉 Player 2</div>
      </div>

      <div className="scores">
        🧠 P1: {scores.player1} | P2: {scores.player2}
      </div>

      <div className="grid">
        {cards.map((card, index) => (
          <div
            key={card.id}
            className={`card ${card.flipped || card.matched ? 'flipped' : ''}`}
            style={{ backgroundColor: card.flipped || card.matched ? card.color : '#ccc' }}
            onClick={() => handleCardClick(index)}
          ></div>
        ))}
      </div>

      {gameOver && (
        <div className="gameover">
          <h2>🎉 Game Over!</h2>
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}

      <div className="history">
        <h3>🗂 Game History</h3>
        <ul>
          {history.map((game, i) => (
            <li key={i}>
              {game.player1} ({game.score1}) vs {game.player2} ({game.score2}) → 🏆 <strong>{game.winner}</strong>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
