import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

export default function Students({ onLogout }) {
  const [students, setStudents] = useState([]);
  const [student, setStudent] = useState({ name: '', course: '' });

  const fetchStudents = async () => {
    const res = await axios.get('http://localhost:5000/api/students');
    setStudents(res.data);
  };

  const saveStudent = async () => {
    await axios.post('http://localhost:5000/api/students', student);
    fetchStudents();
  };

  const deleteStudent = async (id) => {
    await axios.delete(`http://localhost:5000/api/students/${id}`);
    fetchStudents();
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div className="page-center">
      <div className="container">
        <h2>Student Manager</h2>
        <input placeholder="Name" onChange={(e) => setStudent({ ...student, name: e.target.value })} />
        <input placeholder="Course" onChange={(e) => setStudent({ ...student, course: e.target.value })} />
        <button onClick={saveStudent}>Save</button>

        {students.map((s) => (
          <div className="student" key={s._id}>
            <h4>{s.name}</h4>
            <p>{s.course}</p>
            <button onClick={() => deleteStudent(s._id)}>Delete</button>
          </div>
        ))}

        {/* ðŸ”“ Logout button */}
        <button onClick={onLogout} style={{ marginTop: '30px', backgroundColor: '#607d8b' }}>
          Logout
        </button>
      </div>
    </div>
  );
}
